import React from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
       const [password, setPassword] = React.useState("");
        const [email, setEmail] = React.useState("");
        const navigate = useNavigate();
        useEffect(()=>{
            const auth = localStorage.getItem('user');
            if(auth)
            {
                navigate('/')
            }
        })

      const handleLogin = async()=>{
        console.warn("email",email,"password",password)
        let result = await fetch('https://localhost:5000/login',{
            method:'post',
            body:JSON.stringify({email, password}),
            headers: {
                'Content-type':'application/json'
            }
        });
        result = await result.json();
        console.warn(result)
        if(result.name){
                localStorage.setItem("user",JSON.stringify(result));
                navigate("/")
        }else{
            alert("please enter correct detail")
        }
      }
 
        return (
    <div className="login">
     
      <h2>LOGIN</h2>
      <form action="#" method="post" />

      <input 
      type="email" name="email" value={email}
      onChange={(e) => setEmail(e.target.value)}  placeholder="Enter Email" required/>

     <input
        type="password" name="password" value={password} 
        onChange={(e) => setPassword(e.target.value)} placeholder="Enter Password" required/>

      <button onClick={handleLogin} className="appButton" type="button">Login</button>
    </div>
  );
};

export default Login;
